import React from 'react';
import './App.css';

const snoowrap = require('snoowrap');

function App() {

  useEffect(() => {
    var authenticationUrl = snoowrap.getAuthUrl({
      clientId: 'emBhpiG5YkFK9g',
      scope: ['identity', 'wikiread', 'wikiedit'],
      redirectUri: 'http://localhost:3000/auth',
      permanent: false,
      state: 'nKAvbCUe5ZVaSVAcPPfkkiAsKKs' // a random string, this could be validated when the user is redirected back
    });
    // --> 'https://www.reddit.com/api/v1/authorize?client_id=foobarbaz&response_type=code&state= ...'

    window.location = authenticationUrl; // send the user to the authentication url
  }, ['once'])

  return (
    <div className="App">
      <h1>Heyy Yo</h1>
    </div>
  );
}

export default App;
